mapper.update:

update board 
   set title=#{title },
       contents=#{contents }
 where no=#{id }
   and user_id=#{userid }

mapper.delete:


delete
  from board 
 where no = #{id }
   and user_id = #{userId }

mapper.select:

select	no,
		title,
		contents
   from board
  where no = #{id }
    and user_no = #{userId }

mapper.update:

update board
   set hits = hits + 1
 where no=#{id }


update board
   set order_no = order_no + 1
 where group_no = #{groupNo }
   and order_no >= #{orderNo }
