mapper.update:

update board 
   set title=#{title },
       contents=#{contents }
 where id=#{id }
   and user_id=#{userid }

mapper.delete:


delete
  from board 
 where id = #{id }
   and user_id = #{userId }

mapper.select:

select	id,
		title,
		contents
   from board
  where id = #{id }
    and user_id = #{userId }

mapper.update:

update board
   set hit = hit + 1
 where id=#{id }


update board
   set o_no = o_no + 1
 where g_no = #{groupNo }
   and o_no >= #{orderNo }
